from django.contrib.sites.models import Siteimport settingsSITE_URL = Site.objects.get_current().domainSTATIC_URL = settings.STATIC_URLdef GenTopbar(args):	if args['type'] == 'landing_page':		return GenLandingPageTopbar()	def GenLandingPageTopbar():	topbar = {'tag': 'div', 'attributes': {'id': 'c2g_layout_topbar'}, 'content': []}		# Logo	img_link = {'tag': 'a', 'attributes': {'href': SITE_URL}, 'content': []}	img_link['content'].append({'tag': 'img', 'attributes': {'class': 'small_logo', 'alt': 'Class2Go', 'src': STATIC_URL + 'graphics/core/logo_160_35.png'}, 'single': True})	topbar['content'].append(img_link)		# User	topbar['content'].append({'tag': 'input', 'attributes': {'id': 'login_button', 'onclick': "$('#loginreg_canvas').show()", 'type': 'button', 'class': 'cw_button', 'value': 'Login', 'style': 'border: 0px; position: absolute; right: 30px; top: 12px; padding-bottom: 3px; width: 120px; font-size: 9pt;'}})	return topbar