from django.http import HttpResponsefrom django.shortcuts import render_to_responsefrom django.template import Context, loaderfrom django.template import RequestContextdef list(request, course_prefix, course_suffix):	return render_to_response('additional_pages/list.html', {'course_prefix': course_prefix, 'course_suffix': course_suffix, 'request': request}, context_instance=RequestContext(request))	def admin(request, course_prefix, course_suffix):	return render_to_response('additional_pages/admin.html', {'course_prefix': course_prefix, 'course_suffix': course_suffix, 'request': request}, context_instance=RequestContext(request))	def view(request, course_prefix, course_suffix, additional_page_id):	return render_to_response('additional_pages/view.html', {'course_prefix': course_prefix, 'course_suffix': course_suffix, 'additional_page_id':additional_page_id, 'request': request}, context_instance=RequestContext(request))	def edit(request, course_prefix, course_suffix, additional_page_id):	return render_to_response('additional_pages/edit.html', {'course_prefix': course_prefix, 'course_suffix': course_suffix, 'additional_page_id':additional_page_id, 'request': request}, context_instance=RequestContext(request))