from django.http import HttpResponsefrom django.shortcuts import render_to_responsefrom django.template import Context, loaderfrom django.template import RequestContextdef admin(request):	return render_to_response('branches/admin.html', {'request': request}, context_instance=RequestContext(request))	def view(request):	return render_to_response('branches/view.html', {'request': request}, context_instance=RequestContext(request))